sys_transform_map.script:
/**
 * For variables go to: http://wiki.service-now.com/index.php?title=Import_Sets_portal
 **/ 
target.team = "Clippers";
target.game_date = getGameDate(source.u_date);

function getGameDate(d) {
   var t = d.split(" ");
   var fixed = resolveMonth(t[1]) + "-" + t[2] + "-" + t[3];
   return fixed;
}

function resolveMonth(d) {
var r;
    if(d == "Jan") {
       r = "01";
    }
    if(d == "Feb") {
       r = "02";
    }
    if(d == "Mar") {
       r = "03";
    }
    if(d == "Apr") {
      r = "04";
    }
    if(d == "May") {
      r = "05";
    }
    if(d == "Jun") {
      r = "06";
    }
    if(d == "Jul") {
      r = "07";
    }
    if(d == "Aug") {
      r = "08";
    }
    if(d == "Sep") {
      r = "09";
    }
    if(d == "Oct") {
      r = "10";
    }
    if(d == "Nov") {
      r = "11";
    }
    if(d == "Dec") {
      r = "12";
    }	
return r;
}
